mapper.update:
UPDATE APPROVAL_FORM 
	SET FORM_NAME = #{form_name}, FORM_CONTENT=#{form_content}
	WHERE FORM_ID = #{form_id}

UPDATE APPROVAL_FORM 
	SET USE_YN = 'N'
	WHERE FORM_ID = #{form_id}

mapper.select:
SELECT FORM_ID, CATEGORY_ID, FORM_NAME, FORM_CONTENT, CREATE_DATE
FROM APPROVAL_FORM
WHERE FORM_ID = #{form_id}
