mapper.update:
UPDATE APPROVAL_LINE 
SET STATUS_ID = 'ST04', APPROVAL_TIME = CURRENT_DATE
WHERE APPROVAL_ID = (SELECT APPROVAL_ID FROM APPROVAL a 
		WHERE a.APPROVAL_ID = #{approval_id} 
		AND APPROVAL_STATUS = 'ST02')
AND APPROVER_EMPNO = #{empno}

UPDATE APPROVAL_LINE 
SET STATUS_ID = 'ST05', APPROVAL_TIME = CURRENT_DATE

,REJECT_REASON = #{reject_reason}

WHERE APPROVAL_ID = (SELECT APPROVAL_ID FROM APPROVAL a 
		WHERE a.APPROVAL_ID = #{approval_id} 
		AND APPROVAL_STATUS = 'ST02')
AND APPROVER_EMPNO = #{empno}

mapper.select:
SELECT COUNT(*) FROM APPROVAL_LINE WHERE APPROVAL_ID = #{approval_id}
AND STATUS_id = 'ST04'
AND STATUS_id = 'ST05'

SELECT COUNT(*) FROM APPROVAL_LINE WHERE APPROVAL_ID = #{approval_id}
AND STATUS_ID = 'ST04'
AND STATUS_ID = 'ST05'

SELECT LINE_NAME, LINE_DATA
	FROM SAVE_LINE
	WHERE EMPNO = #{empno}
