mapper.insert:
INSERT INTO SCHEDULE (
    SCH_ID, EMPNO, SCH_TITLE, SCH_STARTDATE, SCH_ENDDATE, SCH_COLOR
) VALUES (
    SCH_ID_SEQ.NEXTVAL, #{empno}, #{sch_title}, #{sch_startdate}, #{sch_enddate}, #{sch_color}
)

mapper.update:
    	UPDATE SCHEDULE
		SET UPDATE_EMPNO=#{empno}, UPDATE_DATE=CURRENT_DATE, USE_YN='N' 
		WHERE SCH_ID=#{sch_id}
