mapper.update:
  	UPDATE DEPARTMENTS


  			DEPTNAME = #{deptname}


  			PARENT_DEPTNO = #{parent_deptno}

  		UPDATE_DATE=TO_DATE(TO_CHAR(SYSDATE, 'YY-MM-DD')), UPDATE_EMP=#{update_emp}

		WHERE DEPTNO=#{deptno}

  UPDATE DEPARTMENTS
	SET USE_YN='N', UPDATE_DATE=TO_DATE(TO_CHAR(SYSDATE, 'YY-MM-DD')), UPDATE_EMP=#{update_emp}
	WHERE DEPTNO=#{deptno}
